import { styled, Box, Typography } from '@mui/material';
import backgroundImage from '../../images/blog-background.jpg';

const Image = styled(Box)`
    width: 100%;
    background-size: contain;
    background: url(${backgroundImage}) center/cover no-repeat #000;
    height: 40vh;
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;
`;

const Heading = styled(Typography)`
    font-size: 70px; 
    height: 70vh;
    padding: 10px 20px; /* Add padding to increase readability */
    border-radius: 5px; /* Add border-radius for rounded corners */
    margin-bottom: 20px; /* Add margin bottom to create space between the heading and subheading */
    /* Add more styles to customize appearance */
    text-shadow: 2px 2px 4px rgba(0, 0, 0, 0.5); /* Add text shadow for better readability */
    font-weight: bold; /* Make text bold */
`;

const Banner = () => {
    
    return (
        <Image>
            <Heading >The Voice Canvas</Heading>
            
        </Image>
    )
}

export default Banner;
